const today = new Date().toISOString().slice(0,10);

// Get the tasks string
const input = $json.tasks || "";

// Split by either newlines OR " |" patterns OR multiple spaces
const lines = input.split(/ (?=\w+\|)|\n/);

let completed = [];
let incomplete = [];

for (const line of lines) {
  if (!line.trim()) continue;
  const [task, status, dueDate] = line.split("|").map(x => x.trim());

  if (dueDate === today) {
    if (status.toLowerCase() === "complete") {
      completed.push(task);
    } else {
      incomplete.push({ Task: task, Status: status, DueDate: dueDate });
    }
  }
}

return [{ json: { completed, incomplete } }];

// Get tomorrow's date in YYYY-MM-DD format
const tomorrow = new Date();
tomorrow.setDate(tomorrow.getDate() + 1);
const nextDate = tomorrow.toISOString().slice(0,10);

// Update incomplete tasks to have DueDate = tomorrow
const rolledOver = $json.incomplete.map(t => ({
  Task: t.Task,
  Status: "Incomplete",
  DueDate: nextDate
}));

// Return both completed and updated incomplete tasks
return [{ json: { completed: $json.completed, incomplete: rolledOver } }];

